exports.directoryUrl = function () {
  const postCode = require('./postcodesArray.js');
  const categories = require('./directoryCategories.js');
  const locationUrl = postCode.postcodes;
  const location = exports.randomItem(locationUrl);
  const arr = categories.categories;
  const category = exports.randomItem(arr);
  const versionArr = ['search', 'find'];
  const version = exports.randomItem(versionArr);
  return `/${version}/${category}${location}`;
};

exports.sort_by = function (field, reverse) {
  const key = function (x) { return x[field]; };
  reverse = !reverse ? 1 : -1;
  return function (a, b) {
    return a = key(a), b = key(b), reverse * ((a > b) - (b > a));
  };
};

//Adding Commands from libraries to your browser instance
exports.addCommands = function (client, lib) {
    return Object.getOwnPropertyNames(lib).forEach((func) => {
        client.addCommand(func.toString(), lib[func]);
    });
};

// Function to randomise options available in an array
exports.randomItem = function (arr) {
    return  arr[Math.floor(Math.random() * arr.length)];
};